export function centerWordHorizontal(width, word) {
  const characters = word.split('');
  const middle = width / 2;
  const centeredWordPosition = middle - (characters.length / 2 + 8);
  return centeredWordPosition;
}
export function centeredWordVertical(heigth, word) {
  const middle = heigth / 2;
  const centeredWordPosition = middle - 8 / 2;
  return centeredWordPosition;
}
export function calculateHalfOfHalf(input) {
  const half = input / 2;
  return half / 2;
}
